import React, { useState } from 'react';

function App() {
  const [points, SetPoints] = useState(0);
  const [cash, SetCash] = useState(0);
  const [sellPrice, SetSellPrice] = useState(0.5);
  const [rate, SetRate] = useState(1);
  const [upgradeCost, SetUpgradeCost] = useState(2);
  const [upgradeCount, SetUpgradeCount] = useState(0);

  const Sell = () => {
    if (points > 0) {
      SetCash(points * sellPrice);
      for (let i = 0; i < points; i++) {
        SetSellPrice(sellPrice * (Math.random() + 0.5))
      }
      SetPoints(0);
    }
  }

  const UpgradeRate = () => {
    if (cash >= upgradeCost) {
      SetCash(cash - upgradeCost);
      // if (cash < 25) { // exponential first section
      //   SetRate(0-((rate - Math.sqrt(25))**2) + 25) // -(x-2)^2 + 4
      // }
      // if (cash < 100) {

      // }
      // else if (cash > 25 && cash < 200)
      SetRate(rate + 0.25);

      SetUpgradeCount(upgradeCount + 1);
      SetUpgradeCost(upgradeCost + 0.05 *(upgradeCount ** 2)) // 0.05x^2
    }

  }

  return (
    <div className="App">
      {/* <h1 className="title has-text-centered"></h1> */}
      <br></br>
      <br></br>
      <div className="columns is-centered">
        <div className="column has-text-centered">
          <h1 className="title is-1">Title</h1>
          <h3 className="subtitle is-5">cash: ${parseFloat(cash).toFixed(2)}</h3>
        </div>
      </div>
      <br></br>
      <br></br>
      <div className="columns is-centered">

        <div className="column is-3 box has-text-centered">
          <h3 className="subtitle is-3">Upgrades</h3>
          <h3 className="subtitle is-5">rate: {parseFloat(rate).toFixed(1)}</h3>
          <div className="button is-dark" onClick={UpgradeRate}>Upgrade</div>
          <h3 className="subtitle is-5">cost: ${parseFloat(upgradeCost).toFixed(2)}</h3>
        </div>

        <div className="column is-1"></div>

        <div className="column box is-3 has-text-centered">
          <h3 className="subtitle is-3">Collect + sell</h3>
          <div>
          <h3 className="subtitle is-5">points: {parseInt(points)}</h3>
          </div>
          <div className="button is-dark" onClick={() => SetPoints(points + (1 * rate))}>Collect</div><br></br>
          {/* <div className="button is-black" onClick={() => SetPoints(points + 1)}>Collect</div><br></br> */}
          <br></br>
          <br></br>
          <div className="button is-warning" onClick={Sell}>Sell</div><br></br>
          <h3 className="subtitle is-5">sell price: ${parseFloat(sellPrice).toFixed(2)}</h3>

          {/* <div className="button is-gray" onClick={() => SetRate(rate * 1.1)}>level up</div> */}
        </div>

      </div>
    </div>
  );
}

export default App;
