import React, { useState } from 'react';

function App() {
  const [points, SetPoints] = useState(0);
  const [cash, SetCash] = useState(0);
  const [sellPrice, SetSellPrice] = useState(0.5);

  const Sell = () => {
    SetCash(points * sellPrice);
    SetPoints(0);
    SetSellPrice(sellPrice * (Math.random() + 0.5))
  }
  // const [rate, SetRate] = useState(1);
  return (
    <div className="App">
      {/* <h1 className="title has-text-centered"></h1> */}
      <br></br>
      <br></br>
        <h1 className="title has-text-centered">Title</h1>
      <br></br>
      <br></br>
      <div className="columns is-centered">
        <div className="column is-3 has-text-centered">
          {/* <div className="button is-black" onClick={() => SetPoints(points + (1 * rate))}>Collect</div><br></br> */}
          <div className="button is-black" onClick={() => SetPoints(points + 1)}>Collect</div><br></br>
          <div className="button is-warning" onClick={Sell}>Sell</div><br></br>

          {/* <div className="button is-gray" onClick={() => SetRate(rate * 1.1)}>level up</div> */}
          <br></br>
          <br></br>
          <br></br>
          <br></br>
          {/* <h3 className="subtitle is-3">points: {parseFloat(points).toFixed(1)}</h3> */}
          <h3 className="subtitle is-3">points: {parseInt(points)}</h3>
          {/* <h3 className="subtitle is-3">rate: {parseFloat(rate).toFixed(2)}</h3> */}
          <h3 className="subtitle is-3">cash: ${parseFloat(cash).toFixed(2)}</h3>
          <h3 className="subtitle is-3">sell price: ${parseFloat(sellPrice).toFixed(2)}</h3>
        </div>

      </div>
    </div>
  );
}

export default App;
